mapper.update:
UPDATE pro_board
SET viewCnt = viewCnt+1
WHERE no = #{no}

UPDATE pro_board
SET 
	title = #{title}
	,content = #{content}
	,writer = #{writer}
	,updateDate = SYSDATE
	,fileOpenYn = #{fileOpenYn}
WHERE no = #{no}

UPDATE pro_board
SET deleteYn = 'y'
WHERE no = #{no}

mapper.select:
SELECT COUNT(*) cnt
FROM pro_board
WHERE boardname = #{boardname}
